import React, { useEffect, useState } from "react";
import "./MainPage.css";
import ArrowBackIcon from "@mui/icons-material/ArrowBack";
import { useNavigate } from "react-router-dom";
import useLoggedInUser from "../../../hooks/useLoggedInUser";
import CenterFocusWeakIcon from "@mui/icons-material/CenterFocusWeak";
import MyLocationIcon from '@mui/icons-material/MyLocation';
import AddLinkIcon from '@mui/icons-material/AddLink';
import Post from "../../Feed/Post/Post";
function MainPage({ user }) {
  const navigate = useNavigate();
  const username = user?.email?.split("@")[0];
  const [ loggedInUser ] = useLoggedInUser();

  const handleUploadCoverImage = () => {
    console.log("object");
  };

  const handleUploadProfileImage = () => {
    console.log("object");
  };
  const [posts, setposts] = useState([]);
  useEffect(() => {
    fetch("http://localhost:5000/post")
    .then((res)=>res.json())
    .then((data)=>{
      setposts(data)
    })
  }, [posts]);
  return (
    <div>
      <ArrowBackIcon
        className="arrow_icon"
        onClick={() => {
          navigate("/");
        }}
      />
      <h4>{username}</h4>
      <div className="mainProfile">
        <div className="profile-bio">
          {
            <div>
              <div className="coverImageContainer">
                <img
                  src={
                    loggedInUser[0]?.coverImage
                      ? loggedInUser[0]?.coverImage
                      : "https://www.proactivechannel.com/files/BrandImages/Default.jpg"
                  }
                  alt=""
                  className="coverImage"
                />
                <div className="hoverCoverImage">
                  <label htmlFor="image" className="imageIcon">
                    <CenterFocusWeakIcon className="photoIcon" />
                  </label>
                  <div className="imageIcon_tweetButton">
                    <input
                      type="file"
                      id="image"
                      className="imageInput"
                      onChange={handleUploadCoverImage}
                    />
                  </div>
                </div>
              </div>
              <div className="avatar-img">
                <div className="avatarContainer">
                  <img
                    src={
                      loggedInUser[0]?.coverImage
                        ? loggedInUser[0]?.coverImage
                        : "https://cdn.pixabay.com/photo/2016/08/08/09/17/avatar-1577909_960_720.png"
                    }
                    alt=""
                    className="avatar"
                  />
                </div>
                <div className="hoverAvatarImage">
                  <div className="imageIcon_tweetButton">
                    <label htmlFor="profileImage" className="imageIcon">
                      <CenterFocusWeakIcon className="photoIcon" />
                    </label>
                    <div className="imageIcon_tweetButton">
                      <input
                        type="file"
                        id="profileImage"
                        className="imageInput"
                        onChange={handleUploadProfileImage}
                      />
                    </div>
                  </div>
                </div>
                <div className="userInfo">
                  <div>
                    <h3 className="heading-3">
                      {loggedInUser[0]?.name
                        ? loggedInUser[0]?.name
                        : user && user?.displayName}
                    </h3>
                    <p className="usernameSection">@{username}</p>
                  </div>
                  <div className="infoContainer">
                    {loggedInUser[0]?.bio ? loggedInUser[0]?.bio : ""}
                    <div className="locationAndLink">
                      {loggedInUser[0]?.location ? (
                        <p className="sunInfo"><MyLocationIcon/>{loggedInUser[0]?.location}</p>
                      ) : (
                        ""
                      )}
                      {loggedInUser[0]?.website ? (
                        <p className="sunInfo"><AddLinkIcon/>{loggedInUser[0]?.website}</p>
                      ) : (
                        ""
                      )}
                    </div>
                  </div>
                  <h4 className="tweetsText">Tweets</h4>
                  <hr />
                </div>
                {
                    posts.map((p)=><Post id={p._id} p = { p }/>)
                }
              </div>
            </div>
          }
        </div>
      </div>
    </div>
  );
}

export default MainPage;
