import * as React from "react";
import "./EditProfile.css";
import { Box, IconButton, Modal, TextField } from "@mui/material";
import CloseIcon from "@mui/icons-material/Close";
function EditProfile({ user, loggedInUser }) {
  const style = {
    position: "absolute",
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    width: 600,
    height: 600,
    bgcolor: "background.paper",
    boxShadow: 24,
    borderRadius: 8,
  };
  function EditChild ({ dob, setdob}) {
    
  }
  const [open, setopen] = React.useState(false);
  const [name, setname] = React.useState(false);
  const [bio, setbio] = React.useState(false);
  const [location, setlocation] = React.useState(false);
  const [website, setwebsite] = React.useState(false);
  const [dob, setdob] = React.useState(false);
  const handleSave = () => {
    console.log("saving");
    setopen(false);
  };
  return (
    <div>
      <button className="Edit-profile-btn" onClick={() => setopen(true)}>
        Edit Profile
      </button>
      <Modal
        open={open}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box sx={style} className="modal">
          <div className="header">
            <IconButton
              onClick={() => {
                setopen(false);
              }}
            >
              <CloseIcon />
            </IconButton>
            <h2 className="header-title">Edit Profile</h2>
            <button className="save-btn" onClick={handleSave}>
              Save
            </button>
          </div>
          <form className="fill-content">
            <TextField
              className="text-field"
              fullWidth
              label="Name"
              is="fullWidth"
              variant="filled"
              onChange={(e) => setname(e.target.value)}
              defaultValue={loggedInUser[0]?.name ? loggedInUser[0]?.name : ""}
            />
            <TextField
              className="text-field"
              fullWidth
              label="Bio"
              is="fullWidth"
              variant="filled"
              onChange={(e) => setbio(e.target.value)}
              defaultValue={loggedInUser[0]?.bio ? loggedInUser[0]?.bio : ""}
            />
            <TextField
              className="text-field"
              fullWidth
              label="Location"
              is="fullWidth"
              variant="filled"
              onChange={(e) => setlocation(e.target.value)}
              defaultValue={loggedInUser[0]?.location ? loggedInUser[0]?.location : ""}
            />
            <TextField
              className="text-field"
              fullWidth
              label="Website"
              is="fullWidth"
              variant="filled"
              onChange={(e) => setwebsite(e.target.value)}
              defaultValue={loggedInUser[0]?.website ? loggedInUser[0]?.website : ""}
            />
          </form>
          <div className="birthdate-section">
            <p>Birth Date</p>
            <p>.</p>
            <EditChild dob= { dob } setdob = { setdob }/>
          </div>
          <div className="last-section"></div>
        </Box>
      </Modal>
    </div>
  );
}

export default EditProfile;
