import { Avatar, Button } from "@mui/material";
import React, { useState } from "react";
import AddPhotoAlternateIcon from "@mui/icons-material/AddPhotoAlternate";
import "./TweetBox.css";
import axios from "axios";
import useLoggedInUser from "../../hooks/useLoggedInUser";

function Tweetbox() {
  const [post, setpost] = useState("");
  const [imageURL, setimageURL] = useState("");
  const [isLoading, setisLoading] = useState("");
  const { loggedInUser }= useLoggedInUser();
  console.log(loggedInUser)

  const userProfilePic = loggedInUser[0]?.profileImage?loggedInUser[0]?.profileImage:"https://api.imgbb.com/1/upload?key=4e03c013581c0c5cc53d4d056dc3cc61"
  const handleUploadImage = (e) => {
    setisLoading(true);
    const image = e.target.files[0];
    console.log(image);
    const formData = new FormData();
    formData.set("image", image);

    axios
      .post(
        "https://api.imgbb.com/1/upload?key=4e03c013581c0c5cc53d4d056dc3cc61",
        formData
      )
      .then((res) => {
        setimageURL(res.data.data.display_url);
        console.log(res.data.data.display_url);
        setisLoading(false);
      })
      .catch((error) => {
        console.log(error);
        setisLoading(false);
      });
  };
  const handleTweet = (e) => {
    e.preventDefault();
    if (imageURL) {
      //console.log(post);
      const userPost = {
        post: post,
        photo: imageURL,
      };
      console.log(userPost);
      fetch("http://localhost:5000/post", {
        method: "POST",
        headers: {
          "content-type": "application/json",
        },
        body: JSON.stringify(userPost),
      })
        .then((res) => res.json())
        .then((data) => {
          console.log(data);
        });
    }
  };
  return (
    <div className="tweetBox">
      <form onSubmit={handleTweet}>
        <div className="tweetBox_input">
          <Avatar src="https://cdn.pixabay.com/photo/2016/08/08/09/17/avatar-1577909_960_720.png" />
          <input
            type="text"
            placeholder="What's happening"
            onChange={(e) => setpost(e.target.value)}
          />
        </div>
        <div className="imageIcon_tweetButton">
          <label htmlFor="image" className="imageIcon">
            {isLoading ? (
              <p>Uploading image</p>
            ) : (
              <p>{imageURL ? "image uploaded" : <AddPhotoAlternateIcon />}</p>
            )}
          </label>
          <input
            type="file"
            id="image"
            className="imageInput"
            onChange={handleUploadImage}
          />
          <Button className="tweetBox_tweetButton" type="submit">
            Tweet
          </Button>
        </div>
      </form>
    </div>
  );
}

export default Tweetbox;
